frappe.ui.form.on('Issue', {
	refresh(frm) {
		frm.add_custom_button(__("Timesheets"), function() {
		    frm.save();
            frappe.route_options = {"issue": cur_frm.doc.name};
            frappe.set_route("List", "Timesheet");
	    });
		frm.add_custom_button(__("KB Articles"), function() {
		    frm.save();
            frappe.route_options = {
                "area": "Technical",
                "type": "Product",
                "product": cur_frm.doc.model 
                };
            frappe.set_route("List", "KBA");
	    });
	    frm.add_custom_button(__("Parts Request"), function() {
    	    const request = frappe.model.get_new_doc("Material Request");
    	    var parts;
	        frappe.run_serially([
	            () => console.log("Request started"),
        	    () => frm.save(),
                () => request.material_request_type = "Material Transfer",
                () => request.company = "Eskill Trading (Pvt) Ltd",
                () => request.requested_by = frappe.session.user,
                () => request.issue = frm.doc.name,
                () => console.log("Finished request details, populating item table"),
    	        () => frappe.set_route("Form", "Material Request", request.name)
            ]);
		});
	},

	part_list: function(frm) {
	    console.log("After save");
	    var requests = new Array(0);
	    var test;
	    frappe.call({
	        method: "frappe.client.get_list",
	        args: {
	            doctype: "Material Request",
	            filters: [
	                ['issue', '=', frm.doc.name],
	                ['docstatus', '=', 1]
                ],
	            fields: ['name'],
	            limit_page_length: 500
	        },
	        callback: function(data) {
	            data.message.forEach(function(row) {
	                requests.unshift(row);
	            });
	        }
	    });
	    console.log("Got requests");
	    console.log(test);
	    console.log('requests', requests, requests.length);
	    var partList = frm.doc.part_list;
	    var items = new Array(0);
	    requests.forEach(function(row) {
	        console.log(row, row.length);
    	    frappe.call({
    	        method: "frappe.client.get_list",
    	        args: {
    	            doctype: "Material Request Item",
    	            filters: [
    	                ['parent', '=', row.name],
                    ],
    	            fields: ['item_code', 'qty', 'ordered_qty'],
    	            limit_page_length: 500,
    	            parent: 'Material Request'
    	        },
    	        callback: function(data) {
    	            data.message.forEach(function(item) {
        	            items.push(item);
    	            });
    	        }
    	    });
	    });
	    console.log("Got request items");
	    console.log('items', items, items.length);
	},

	serial_number: function(frm) {
	    var status;
	    frappe.run_serially([
    	    () => console.log("serial number modified"),
    	    () => frappe.call({
                method: "frappe.client.get_value",
                args: {
                    "doctype": "Serial No",
                    "filters": {
                        'name': frm.doc.serial_number // where Clause 
                    },
                    "fieldname": ['maintenance_status'], // fieldname to be fetched
                    "limit": 1
                },
                callback: function (data) {
                    status = data.message.maintenance_status;
                    if (!data.exc) {
                       console.log(data.message);
                    }
                }
            }),
            () => console.log(status),
    	    () => frm.set_df_property("serial_number", "description", status ? status:"Unknown")
	    ]);
	},

	serial_no_report: function(frm) {
	    frm.save();
	    window.open("api/method/frappe.utils.print_format.download_pdf?doctype=Serial No&name=" + frm.doc.serial_number + "&format=Eskill Serial Number History");
	},

	email_account: function(frm) {
	    if (frm.doc.email_account == 'Zabbix Internal Issues') {
	        frm.set_value("issue_type", "Internal Software Support");
	        frm.set_value("priority", "High");
	        frm.save();
	    }
	},

	kba_creation: function(frm) {
	    frm.save();
	    frappe.db.insert({
	        doctype: 'KBA',
	        topic: frm.doc.subject,
	        area: 'Technical',
	        type: 'Product',
	        product: frm.doc.model,
	        details: frm.doc.resolution_details,
	        parent_kba: 'KBA-20-Other-00000036'
	    });
	    frappe.msgprint("KBA created.");
	},

    model: function(frm) {
		frm.fields_dict['serial_number'].get_query = function() {
			return{
				filters: {
					'item_code': frm.doc.model
				}
			};
		};
    },
});

frappe.ui.form.on('Part List', {
	received_button: function(frm, cdt, cdn) {
	    const row = frappe.get_doc(cdt,cdn);
        cur_frm.get_field("part_list").grid.grid_rows[row.idx - 1].doc.status = "Received";
        cur_frm.get_field("part_list").grid.grid_rows[row.idx - 1].refresh_field("status");
	    console.log("Part received");
	}
});

