script:
frappe.ui.form.on('Item', {
	refresh(frm) {
	    frm.add_custom_button(__("KB Articles"), function() {
	        frm.save();
            frappe.route_options = {
                "type": "Product",
                "product": cur_frm.doc.item_code
            };
            frappe.set_route("List", "KBA", "List");
	    });
	    frm.add_custom_button(__("New KBA"), function() {
	        frm.save();
            frappe.route_options = {
                "type": "Product",
                "product": cur_frm.doc.item_code
            };
            frappe.set_route("Form", "KBA", "New KBA");
	    });
    }
});

frappe.ui.form.on('Issue', {
	refresh(frm) {
		frm.add_custom_button(__("Timesheets"), function() {
		    frm.save();
            frappe.route_options = {"issue": cur_frm.doc.name};
            frappe.set_route("List", "Timesheet");
	    });
		frm.add_custom_button(__("KB Articles"), function() {
		    frm.save();
            frappe.route_options = {
                "area": "Technical",
                "type": "Product",
                "product": cur_frm.doc.model 
                };
            frappe.set_route("List", "KBA");
	    });
	},

	email_account: function(frm) {
	    if (frm.doc.email_account == 'Zabbix Internal Issues') {
	        frm.set_value("issue_type", "Internal Software Support");
	        frm.set_value("priority", "High");
	        frm.save();
	    }
	},

	kba_creation: function(frm) {
	    frm.save();
	    frappe.db.insert({
	        doctype: 'KBA',
	        topic: frm.doc.subject,
	        area: 'Technical',
	        type: 'Product',
	        product: frm.doc.model,
	        details: frm.doc.resolution_details,
	        parent_kba: 'KBA-20-Other-00000036'
	    });
	    frappe.msgprint("KBA created.");
	},

    model: function(frm) {
		frm.fields_dict['serial_number'].get_query = function() {
			return{
				filters: {
					'item_code': frm.doc.model
				}
			};
		};
    },
});

frappe.ui.form.on('Sales Order', {
	before_submit(frm) {
    	var tempCust = frm.doc.customer;
		frm.set_value("customer", "");
		frm.set_value("customer", tempCust);

        var emp = '';
        frappe.run_serially([
            () => frappe.call({
                method: "frappe.client.get_value",
                args: {
                    "doctype": "Employee",
                    "filters": {
                        'user_id': frappe.session.user // where Clause 
                    },
                    "fieldname": ['name'], // fieldname to be fetched
                    "limit": 1
                },
                callback: function (data) {
                    emp = data.message.name;
                    if (!data.exc) {
                       console.log(data.message);
                    }
                }
            }),
            () => frappe.call({
                method: "frappe.client.get_value",
                args: {
                    "doctype": "Sales Person",
                    "filters": {
                        'employee': emp // where Clause 
                    },
                    "fieldname": ['name'], // fieldname to be fetched
                    "limit": 1
                },
                callback: function (data) {
                    frm.set_value('sales_person', data.message.name);
                    frm.refresh_field('sales_person');
                    if (!data.exc) {
                       console.log(r.message);
                    }
                }
            }),
        ]);
	}
});

