css:
@font-face {
	font-family: DaxlinePro;
	font-style: normal;
	font-weight: 300;
	src: url(/fonts/DaxlinePro.ttf);
}

@font-face {
	font-family: Neuropol;
	font-style: normal;
	font-weight: 300;
	src: url(/fonts/NeuropolXRG.ttf);
}

.print-format { 
    padding: 0mm !important;
    font-family: 'DaxlinePro';
}

body, p, span, div, element {
    color: #474c51;
    letter-spacing: normal !important;
}

#header-image {
    background-image: url(/files/Eskill_Header.jpg);
    display: table;
    width: 100%;
    background-size: 211mm;
    background-repeat: no-repeat;
    height: 38mm;
    background-position: left top;
    padding-top: 7mm !important;
}

.layout-table, .layout-row, .layout-div {
    margin: 0 !important;
    padding: 0 !important;
    width: 100% !important;
}

.layout-cell {
    margin: 0 !important;
    padding: 0 !important;
    height: 100%;
    vertical-align: top;
}



#print-heading {
    font-size: 18pt;
    font-weight: bold;
    line-height: 1;
    padding: 0mm !important;
    margin-top: 4mm !important;
    margin-right: 6mm !important;
    margin-bottom: 3mm !important;
    text-align: right;
}

#company-info-cell {
    width: 40%;
}

#company-info {
    font-size: 8pt;
    margin-left: 6mm !important;
}

#project-name-cell {
    width: 35%;
    vertical-align: bottom;
}

#tax-info-cell {
    width: 25%;
}

#tax-info {
    font-size: 8pt;
    text-align: right;
    margin-right: 5mm;
    margin-top: 5mm;
}

#tax-info p {
    margin: 0;
    padding: 0;
}

#top-info {
    margin-left: 7mm;
    margin-right: 7mm;
}

#middle-section {
    margin-left: 7mm;
    margin-right: 7mm;
}

#middle-section-div {
    min-height: 12cm;
    height: 12cm;
}

#total-section {
    margin-left: 7mm;
    margin-right: 7mm;
    margin-bottom: 1mm;
    height: 43mm;
    max-height: 43mm;
    min-height: 43mm;
    overflow: hidden;
}

.black-box {
    border: 1px solid #474c51;
    border-radius: 3px;
    padding: 0.5mm;
    margin-left: 1mm;
    margin-bottom: 1mm;
    width: auto;
    height: auto;
    overflow: hidden;
    min-height: 12mm;
}

.yellow-box {
    border: 1px solid #ffbc1f;
    border-radius: 3px;
    background-color: #ffbc1f;
    padding: 0.5mm;
    margin-left: 1mm;
    margin-bottom: 1mm;
    width: auto;
    height: auto;
    overflow: hidden;
    min-height: 12mm;
}

.yellow-bar {
    background-color: #ffbc1f;
    border-radius: 2px;
    width: 100%;
    text-align: center;
    line-height: 1.5;
    font-size: 7pt;
    font-weight: bold;
    color: #252525;
}

.filled-grey-box {
    border: 1px solid #cfcfcf;
    border-radius: 3px;
    background-color: #cfcfcf;
    padding: 1mm;
    margin-left: 1mm;
    margin-bottom: 1mm;
    width: auto;
    height: 12mm;
    overflow: hidden;
    min-height: 12mm;
    max-height: 12mm;
    color: #000000 !important;
}


.black-bar {
    border-bottom: 1px solid #252525;
    width: 100%;
    text-align: center;
    line-height: 1.5;
    font-size: 7pt;
    font-weight: bold;
    color: #252525;
}

.box-content-left {
    padding: 1mm;
    font-size: 9pt;
    text-align: left;
    overflow: hidden;
}

.box-content-center {
    padding: 1mm;
    font-size: 9pt;
    text-align: center;
    overflow: hidden;
}

.box-content-right {
    padding: 1mm;
    font-size: 9pt;
    text-align: right;
    overflow: hidden;
}

#customer-info {
    width: 65%;
    padding: 0 !important;
    height: auto;
}

#customer {
    height: 33mm;
    max-height: 33mm;
    min-height: 33mm;
}

#document-info {
    width: 35%;
    padding: 0 !important;
}

#document-number-cell {
    margin: 0 !important;
    padding: 0!important;
    height: 12mm;
    vertical-align: middle;
}

#page-cell, #date-cell, #other-number-cell, #exchange-rate-cell, #customer-vat-cell, #customer-ref-cell {
    margin: 0 !important;
    padding: 0!important;
    height: 12mm;
    vertical-align: middle;
}

#document-number {
    font-weight: bold;
    font-size: 12pt;
}

#customer-currency-cell {
    width: 10%;
    margin: 0 !important;
    padding: 0!important;
    height: 12mm;
    vertical-align: middle;
}

#salesperson-cell {
    width: 35%;
    margin: 0 !important;
    padding: 0!important;
    height: 12mm;
    vertical-align: middle;
}

#other-number, #customer-currency, #exchange-rate, #customer-vat, #customer-ref, #salesperson {
    font-weight: normal;
    font-size: 9pt;
}

.project-name {
    padding: 0mm;
    font-size: 8pt;
    text-align: center;
    overflow: hidden;
    font-weight: bold;
    vertical-align: middle !important;
    height: 40px !important;
    max-height: 40px !important;
    border-radius: 4px;
}

#project-text {
    display: table;
    float: left;
    text-align: left;
}

#project-text p {
        display: table-cell;
        vertical-align: middle !important;
        height: 40px;
        min-height: 40px;
        max-height: 40px;
}

#project-icon {
    display: inline-block;
    width: 40px !important;
    height: 40px !important;
    max-height: 40px !important;
    object-fit: none;
    float: left;
}

.yellow-head-center {
    background-color: #ffbc1f;
    width: 100%;
    text-align: center;
    line-height: 1.5;
    font-size: 7pt;
    font-weight: bold;
    color: #2a2a2a;
    padding: 0 5px 0 5px;
}

.yellow-head-left {
    background-color: #ffbc1f;
    width: 100%;
    text-align: left;
    line-height: 1.5;
    font-size: 7pt;
    font-weight: bold;
    color: #2a2a2a;
    padding: 0 5px 0 5px;
}

.yellow-head-right {
    background-color: #ffbc1f;
    width: 100%;
    text-align: right;
    line-height: 1.5;
    font-size: 7pt;
    font-weight: bold;
    color: #2a2a2a;
    padding: 0 5px 0 5px;
}

#items {
    table-layout: fixed;
}

#item-line { width: 5% !important; padding: 0 !important; border-right: 1px solid #ffbc1f; }
#item-item { width: 16% !important; padding: 0 !important; border-right: 1px solid #ffbc1f; }
#item-description { width: 45% !important; padding: 0 !important; border-right: 1px solid #ffbc1f; }
#item-qty { width: 5% !important; padding: 0 !important; border-right: 1px solid #ffbc1f; }
#item-price { width: 11% !important; padding: 0 !important; border-right: 1px solid #ffbc1f; }
#item-uom { width: 7% !important; padding: 0 !important; border-right: 1px solid #ffbc1f; }
#item-total { width: 11% !important; padding: 0 !important; }

#item-line, #item-item, #item-qty, #item-uom, #item-price, #item-total, #item-description {
    height: auto;
}

#item-line div, #item-item div, #item-qty div, #item-uom div, #item-price div, #item-total div { 
    overflow: hidden;
    max-height: 12pt;
    height: 12pt;
}

#item-description div.yellow-head-left, #item-description div.yellow-head-center, #item-description div.yellow-head-right {
    max-height: 12pt;
    height: 12pt;
}

.cell-content-center {
    width: 100%;
    text-align: center;
    line-height: 1.5;
    font-size: 8pt !important;
    font-weight: normal;
    padding: 0 5px 0 5px;
}

.cell-content-left {
    width: 100%;
    text-align: left;
    line-height: 1.5;
    font-size: 8pt !important;
    font-weight: normal;
    padding: 0 5px 0 5px;
}

.cell-content-right {
    width: 100%;
    text-align: right;
    line-height: 1.5;
    font-size: 8pt !important;
    font-weight: normal;
    padding: 0 5px 0 5px;
}

#bank-info-cell {
    width: 65%;
    padding: 0 !important;
}

#bank-info {
    height:30mm;
    min-height: 30mm;
    max-height: 30mm;
    overflow: hidden;
}

.bank-account {
    display: inline;
    float: left;
    font-size: 8pt;
    line-height: 1.2;
    margin: 1%;
    width: 23%;
    color: #252525;
}

#totals-info-cell {
    width: 35%;
    padding: 0 !important;
}

#totals-info {
    height: 43mm;
    max-height: 43mm;
    min-height: 43mm;
}

#fiscal-signature {
    height: 10mm;
    min-height: 10mm;
    max-height: 10mm;
    /* border: 1px dashed #494949; */
    border-radius: 3px;
    margin-left: 8mm;
    margin-right: 7mm;
    margin-bottom: 10mm;
}

#footer {
    display: table;
    width: 100%;
    background-image: url(/files/Eskill_Footer.jpg);
    background-size: 211mm;
    background-repeat: no-repeat;
    height: 24mm;
    background-position: left bottom;
}

#customer-signature {
    display: table-cell;
    vertical-align: bottom;
    margin: 0;
    font-size: 8pt;
}

#customer-signature p {
    border-top: 1px dotted #000;
    font-size: 8pt;
    width: 120mm;
    margin-left: 20mm;
    margin-bottom: 6mm;
    text-align: center;
    padding-top: 1mm;
}

.banner120pt {
    display: table;
    min-height: 120pt;
    width: 100%;
    text-align: center;
    opacity: 0.2;
}

.banner240pt {
    display: table;
    min-height: 240pt;
    width: 100%;
    text-align: center;
    opacity: 0.2;
}

#banner {
    display: table-cell;
    vertical-align: middle !important;
}

#banner img {
    vertical-align: middle !important;
}

