{% macro pageheader() %}

<!DOCTYPE html>

<div id="header-image" class="layout-div">
    {% if doc.grand_total >= 0 and not is_return %}
        <div id="print-heading">{{ "DELIVERY NOTE" }}</div>
    {% else %}
        <div id="print-heading" style="color: #ca0000;">{{ "SALES RETURN" }}</div>
    {% endif %}

    <!-- Company Information -->
    <table class="layout-table">
        <tbody>
        <tr class="layout-row">
            <td class="layout-cell" id="company-info-cell">
                <div id="company-info">
                    <p>{{ frappe.db.get_value("Address", "Eskill Trading-Office", "address_line1") }}, {{ frappe.db.get_value("Address", "Eskill Trading-Office", "address_line2") }}, {{ frappe.db.get_value("Address", "Eskill Trading-Office", "city") }}</p>
                    <p>Phone: {{ frappe.db.get_value("Address", "Eskill Trading-Office", "phone") }}<br>Email: {{ frappe.db.get_value("Address", "Eskill Trading-Office", "email_id") }}</p>
                </div>
            </td>


            <td class="layout-cell" id="project-name-cell">
                {% if doc.project %}
                    {% set thisproject= frappe.get_doc("Project", doc.project) %}
                    {% set project_style = 'style=\"font-weight: bold; color: ' ~ thisproject.project_colour ~ '; border: 2px solid ' ~ thisproject.project_colour ~ ';\"' | safe %}
                    <table class="layout-table">
                        <tbody><tr class="layout-row">
                            <td class="layout-cell" style="vertical-align: middle;">
                                <div class="project-name" {{ project_style }}>
                                  <span id="project-icon">
                                    {{ projectsvg(thisproject.project_colour) }}
                                  </span>
                                  <span id="project-text">  
                                    {% set project_title_style = 'style=\"color: ' ~ thisproject.project_colour ~ '; font-weight: bold;\"' | safe %}
                                    <p {{ project_title_style }}>PROJECT<br>{{ doc.project | truncate(27, True) }}</p>

                                  </span>
                                </div>
                            </td>
                        </tr></tbody>
                    </table>
                {% endif %}
            </td>

            <!-- Tax Information -->
            <td class="layout-cell" id="tax-info-cell">
                <div id="tax-info">
                    <p>VAT Number: {{ frappe.db.get_value("Company", "Eskill Trading (Pvt) Ltd", "tax_id") }}</p>
                    <p>BP Number: {{ frappe.db.get_value("Company", "Eskill Trading (Pvt) Ltd", "bp_number") }}</p>
                </div>
            </td>
        </tr>
        </tbody>
    </table>
</div>
<div id="top-info">
    <table class="layout-table">
        <tbody>
        <tr class="layout-row">

            <!-- Customer Name and Address -->
            <td class="layout-cell" id="customer-info">
                <div class="black-box">
                    <div class="yellow-bar">
                        {% if doc.grand_total >= 0 and not is_return %}{{ "Delivered To" }}{% else %}{{ "Returned By" }}{% endif %}
                    </div>
                    <div class="box-content-left" id="customer">{{ doc.customer_name }}<br>{% if doc.address_display %}{{ doc.address_display }}{% endif %}</div>
                </div>
            </td>

            <!-- Document Information -->
            <td class="layout-cell" id="document-info">
                <table class="layout-table">
                    <tbody>
                        <tr class="layout-row">
                            <td class="layout-cell" id="date-cell">
                                <div class="black-box">
                                    <div class="yellow-bar">
                                       {% if doc.grand_total >= 0 and not is_return %}{{ "Delivery Date" }}{% else %}{{ "Return Date" }}{% endif %}
                                    </div>
                                    <div class="box-content-center" id="date">{% if doc.posting_date %}{{ doc.get_formatted('posting_date') }}{% else %}NOT POSTED{% endif %}</div>
                                </div>
                            </td>
                            <td class="layout-cell" id="page-cell">
                                <div class="black-box">
                                    <div class="yellow-bar">Page</div>
                                    <div class="box-content-center" id="page">{{ page_count.value }} of {{ page_total.value }}</div>
                                </div>                                
                            </td>
                        </tr>
                    </tbody>
                </table>
                <table class="layout-table">
                    <tbody>
                        <tr class="layout-row">
                            <td class="layout-cell" id="document-number-cell">
                                <div class="black-box">
                                    <div class="yellow-bar">
                                        {% if doc.grand_total >= 0 and not is_return %}{{ "Delivery Note Number" }}{% else %}{{ "Sales Return Number" }}{% endif %}
                                    </div>
                                    <div class="box-content-center" id="document-number">
                                        {% if doc.name %}{{ doc.name }}{% endif %}
                                    </div>
                                </div>
                            </td>                            
                        </tr>                        
                        <tr class="layout-row">
                            <td class="layout-cell" id="other-number-cell">
                                <div class="black-box">
                                    {% if not doc.items[0].against_sales_order %}
                                              <div class="yellow-bar">Order Number</div>
                                                <div class="box-content-center" id="other-number">
                                                    Not Linked                                      
                                                </div>                                            
                                    {% else %}
                                        <div class="yellow-bar">Order Number</div>
                                        <div class="box-content-center" id="other-number">
                                            {{ doc.items[0].against_sales_order }}                                      
                                        </div>
                                    {% endif %}
                                </div>
                            </td>                            
                        </tr>
                    </tbody>
                </table>
            </td>

        </tr>
        </tbody>
    </table>
    <table class="layout-table">
        <tbody>
            <tr class="layout-row">
                <td class="layout-cell" id="transporter-cell">
                    <div class="black-box">
                        <div class="yellow-bar">Transporter</div>
                        <div class="box-content-center" id="transporter">
                            {% if doc.transporter_name %}{{ doc.transporter_name }}{% endif %}
                        </div>
                    </div>                    
                </td>
                <td class="layout-cell" id="driver-cell">
                    <div class="black-box">
                        <div class="yellow-bar">Driver</div>
                        <div class="box-content-center" id="driver">
                           {% if doc.driver_name %}{{ doc.driver_name }}{% endif %}
                        </div>
                    </div>                    
                </td>
                <td class="layout-cell" id="customer-ref-cell">
                    <div class="black-box">
                        {% if doc.grand_total >= 0 and not is_return %}
                            <div class="yellow-bar">Customer PO/Ref</div>
                            <div class="box-content-center" id="customer-ref">
                                {{ doc.po_no }}
                            </div>
                        {% else %}
                            <div class="yellow-bar">Return Against</div>
                            <div class="box-content-center" id="customer-ref">
                                {{ doc.return_against }}
                            </div>   
                        {% endif %}
                    </div>                    
                </td>
                <td class="layout-cell" id="salesperson-cell">
                    <div class="black-box">
                        <div class="yellow-bar">Salesperson</div>
                        <div class="box-content-center" id="salesperson">
                            {{ doc.sales_person |default("Eskill Sales Team", true) }}
                        </div>
                    </div>                    
                </td>


            </tr>
        </tbody>
    </table>


</div>
<div id="middle-section">
    <div class="black-box" id="middle-section-div">
        <table id="items" class="layout-table">
            <tbody>
                <tr class="layout-row">
                    <td class="layout-cell" id="item-line"><div class="yellow-head-left" style="border-top-left-radius: 2px; border-bottom-left-radius: 2px;">Line</div></td>
                    <td class="layout-cell" id="item-item"><div class="yellow-head-left">Item</div></td>
                    <td class="layout-cell" id="item-description"><div class="yellow-head-left">Description</div></td>
                    <td class="layout-cell" id="item-weight"><div class="yellow-head-right">Weight (U)</div></td>
                    <td class="layout-cell" id="item-weight-total"><div class="yellow-head-right">Weight (T)</div></td>
                    <td class="layout-cell" id="item-qty"><div class="yellow-head-right">Qty</div></td>
                    <td class="layout-cell" id="item-uom"><div class="yellow-head-center" style="border-top-right-radius: 2px; border-bottom-right-radius: 2px;">UOM</div></td>
                </tr>

{% endmacro %}


{% macro pagefooter() %}
    </tbody></table>
    </div>
</div>
<div id="total-section">
    <table class="layout-table">
        <tr class="layout-row">
            <td class="layout-cell" id="instructions-cell">
                <div class="yellow-box" id="instructions">
                    <div class="yellow-bar">Instructions</div>
                        <div class="instructions-text">
                            {% if doc.instructions %}{{ doc.instructions | truncate(480, false) }}{% endif %}
                        </div>
                </div>
                <div class="filled-grey-box">
                    <div class="box-content-center" style="font-size: 7pt; color: #000000 !important;">
                        {% if doc.terms %}{{ doc.terms }}{% endif %}
                    </div>
                </div>
            </td>
            <td class="layout-cell" id="totals-info-cell">
                <div class="black-box" id="totals-info">
                    <table class="layout-table">
                        <tr class="layout-row" style="border-top: 2px solid #ffbc1f;">
                            <td class="layout-cell"><div class="cell-content-left" style="font-size: 11pt;"><strong>TOTAL QUANTITY</strong></div></td>
                            <td class="layout-cell"><div class="cell-content-right" style="font-size: 11pt;"><strong>{{ doc.total_qty }}</strong></div></td>
                        </tr>
                        {% if doc.total_net_weight %}
                        <tr class="layout-row">
                            <td class="layout-cell"><div class="cell-content-left" style="font-size: 11pt;"><strong>TOTAL NET WEIGHT</strong></div></td>
                            <td class="layout-cell"><div class="cell-content-right" style="font-size: 11pt;"><strong>{{ doc.total_net_weight }}</strong></div></td>
                        </tr>
                        {% endif %}
                    </table>

                </div>

            </td>
        </tr>
    </table>
</div>

<div id="fiscal-signature"></div>
<div id="footer">
    <div id="customer-signature">
        {% if doc.grand_total >= 0 and not is_return %}
            <p>Items Received In Good Order (Customer Signature)</p>
        {% else %}
            <p>Items Returned In Good Order (Stock Controller Signature)</p>
        {% endif %}
    </div>
</div>

{% endmacro %}

{% set line_total = namespace(value=0) %}
{% set page_total = namespace(value=0) %}
{% set page_count = namespace(value=1) %}
{% set line_count = namespace(value=0) %}
{% set free_line_count = namespace(value=0) %}
{% set break_now = namespace(value=0) %}

{% for item in doc.items %}
    {% set wrapped_description %}
        {{ item.item_name | wordwrap(50,true,'<br>') }}

        {% if item.reason_for_return %}
            <br>{{ '(' ~ item.reason_for_return ~ ')' | wordwrap(50,true,'<br>') }})
        {% endif %}

        {% if item.serial_no %}
            <br><em>
            {% set serial_list = item.serial_no.split('\n') %}
            {% for serial in serial_list %}
                {{ serial | truncate(50,true) }}
                {% if not loop.last %}<br>{% endif %}
            {% endfor %}
            </em>
        {% endif %}
    {% endset %}
    {% set line_list = wrapped_description.split('<br>') %}
    {% set this_line_count | float %}{{ line_list | count }}{% endset %}
    {% set line_total.value = line_total.value + this_line_count %}
    {% set page_float = line_total.value / 27 %}
    {% set page_total.value = page_float | round(0, 'ceil') | int %}
{% endfor %}

{{ pageheader() }}

{% for item in doc.items %}
    {% set itemloop = loop %}
    {% set wrapped_description %}
        {{ item.item_name | wordwrap(50,true,'<br>') }}

        {% if item.reason_for_return %}
            {% set item_reason = '(' ~ item.reason_for_return ~ ')' %}
            <br>{{ item_reason | wordwrap(50,true,'<br>') }}
        {% endif %}

        {% if item.serial_no %}
            <br><em>
            {% set serial_list = item.serial_no.split('\n') %}
            {% for serial in serial_list %}
                {{ serial | truncate(50,true) }}
                {% if not loop.last %}<br>{% endif %}
            {% endfor %}
            </em>
        {% endif %}
    {% endset %}
    {% set line_list = wrapped_description.split('<br>') %}
    {% set this_line_count | float %}{{ line_list | count }}{% endset %}
    {% if line_count.value == 27 %}
        {{ pagefooter() }}
        <div style="page-break-before: always;"></div>
        {% set page_count.value = page_count.value + 1 %}
        {% set line_count.value = 0 %}
        {{ pageheader() }}
    {% endif %}
    {% if loop.last %}
        {% set free_line_count.value = 27 - line_count.value - this_line_count %}
        {% if free_line_count.value >= 0 %}
            <tr class="layout-row" style="border-bottom: 3px solid #ffbc1f;">
        {% else %}
            <tr class="layout-row">
        {% endif %}
    {% else %}
        <tr class="layout-row">
    {% endif %}

            <td class="layout-cell" id="item-line"><div class="cell-content-left">{{ item.idx }}</div></td>
            <td class="layout-cell" id="item-item"><div class="cell-content-left">{{ item.item_code | truncate(15, True) }}</div></td>
            <td class="layout-cell" id="item-description">
                <div class="cell-content-left">
                    {% for line in line_list %}
                        {{ line }}
                        {% if not loop.last and line_count.value < 27 %}<br>{% endif %}
                        {% set line_count.value = line_count.value + 1 %}
                        {% if line_count.value == 27 and not loop.last %}
                            </div></td>
                            <td class="layout-cell" id="item-weight"><div class="cell-content-right">{% if item.weight_per_unit %}{{ item.weight_per_unit  }}{{ item.weight_uom }}{% endif %}</div></td>
                            <td class="layout-cell" id="item-weight-total"><div class="cell-content-right">{% if item.total_weight %}{{ item.total_weight }}{{ item.weight_uom }}{% endif %}</div></td>                            
                            <td class="layout-cell" id="item-qty"><div class="cell-content-right">{% if item.qty is divisibleby(1) %}{{ item.qty | int }}{% else %}{{ item.qty }}{% endif %}</div></td>
                            <td class="layout-cell" id="item-uom"><div class="cell-content-center">{{ item.uom }}</div></td>
                            </tr>
                            {% set break_now.value = 1 %}
                            {{ pagefooter() }}
                            <div style="page-break-before: always;"></div>
                            {% set page_count.value = page_count.value + 1 %}
                            {% set line_count.value = 0 %}
                            {{ pageheader() }}
                            {% if itemloop.last %}
                                <tr class="layout-row" style="border-bottom: 3px solid #ffbc1f;">
                                {% set free_line_count.value = 27 - line_count.value - this_line_count + loop.index %}
                            {% else %}
                                <tr class="layout-row">
                            {% endif %}
                                <td class="layout-cell" id="item-line"><div class="cell-content-left"></div></td>
                                <td class="layout-cell" id="item-item"><div class="cell-content-left"></div></td>
                                <td class="layout-cell" id="item-description">
                                    <div class="cell-content-left">
                        {% endif %}
                    {% endfor %}
                </div>
            </td>
            <td class="layout-cell" id="item-weight"><div class="cell-content-right">{% if break_now.value != 1 and item.weight_per_unit %}{{ item.weight_per_unit }}{{ item.weight_uom }}{% endif %}</div></td>
            <td class="layout-cell" id="item-weight-total"><div class="cell-content-right">{% if break_now.value != 1 and item.total_weight %}{{ item.total_weight }}{{ item.weight_uom }}{% endif %}</div></td>
            <td class="layout-cell" id="item-qty"><div class="cell-content-right">{% if break_now.value != 1 %}{% if item.qty is divisibleby(1) %}{{ item.qty | int }}{% else %}{{ item.qty }}{% endif %}{% endif %}</div></td>
            <td class="layout-cell" id="item-uom"><div class="cell-content-center">{% if break_now.value != 1 %}{{ item.uom }}{% endif %}</div></td>
            {% set break_now.value = 0 %}
        </tr>

    {% if loop.last and free_line_count.value > 9 and free_line_count.value < 20 %}
        </tbody></table>
        {% set divheight = free_line_count.value * 12 %}
        {% set divstyle = 'style=\"height: ' ~ divheight ~ 'pt;\"' %}
        <div class="banner120pt" {{ divstyle }}>
            {% set bannerlist = namespace(path=[]) %}
            {% set banners = frappe.get_all('Banner', filters={'allow_for_print': '1', 'banner_size': 'Height 160px/120pt'}, fields=['file_path']) %}
            {% for banner in banners %}
                {% if loop.first %}
                    {% set bannerlist.path = [ banner.file_path ] %}
                {% else %}
                    {% set bannerlist.path = [ bannerlist.path, banner.file_path ] %}
                {% endif %}
            {% endfor %}
            {% set banner_path = bannerlist.path|random %}
            {% set banner_image = '<img src=\"' ~ banner_path ~ '\">' | safe %}
            {% if banner_path %}<div id="banner">{{ banner_image }}</div>{% endif %}
        </div>


    {% elif loop.last and free_line_count.value >= 20 %}
        </tbody></table>       
        {% set divheight = free_line_count.value * 12 %}
        {% set divstyle = 'style=\"height: ' ~ divheight ~ 'pt;\"' %}
        <div class="banner240pt" {{ divstyle }}>
            {% set bannerlist = namespace(path=[]) %}
            {% set banners = frappe.get_all('Banner', filters={'allow_for_print': '1', 'banner_size': 'Height 320px/240pt'}, fields=['file_path']) %}
            {% for banner in banners %}
                {% if loop.first %}
                    {% set bannerlist.path = [ banner.file_path ] %}
                {% else %}
                    {% set bannerlist.path = [ bannerlist.path, banner.file_path ] %}
                {% endif %}
            {% endfor %}
            {% set banner_path = bannerlist.path|random %}
            {% set banner_image = '<img src=\"' ~ banner_path ~ '\">' | safe %}
            {% if banner_path %}<span id="banner">{{ banner_image }}</span>{% endif %}            
        </div>


    {% endif %}
{% endfor %}

{{ pagefooter() }}

