html:
{% macro pageheader() %}

<div id="header-image" class="layout-div">
    <div id="print-heading">{{ doc.employee_name }}<br>
        {% if doc.start_date and doc.end_date %}
            {% if doc.start_date == doc.end_date %}
                {{ frappe.utils.formatdate(doc.start_date, 'dd-MM-yyyy') }}
            {% else %}
                {{ frappe.utils.formatdate(doc.start_date, 'dd-MM-yyyy') }}
                &#8594;
                {{ frappe.utils.formatdate(doc.end_date, 'dd-MM-yyyy') }}
            {% endif %}
        {% endif %}
    </div>
</div>

<div>
    <div class="section">
        <div class="black-box" id="issue">
            <div class="yellow-bar">
                Issue
            </div>
            <div class="box-content-center">
                {% if doc.issue %}
                    <div class="showmore">
                        {{ frappe.utils.get_link_to_form('Issue', doc.issue) }}
                        {% set thisissue = frappe.get_doc("Issue", doc.issue) %}
                        <span class="showmoretext">{{ thisissue.subject | replace('<br>',' ') | truncate(250,true) }}</span>
                    </div>
                {% else %}
                    {{ "None" }}
                {% endif %}
            </div>
        </div>
        <div class="black-box" id="customer-name">
            <div class="yellow-bar">
                Customer
            </div>
            <div class="box-content-center">
                {% if doc.customer %}
                    {% set thiscustomer = frappe.get_doc("Customer", doc.customer) %}
                    {{ frappe.utils.get_link_to_form('Customer', doc.customer) }}
                {% elif not doc.customer and doc.issue %}
                    {% if thisissue.customer %}
                        {{ frappe.utils.get_link_to_form('Customer', thisissue.customer) }}
                    {% else %}
                        {{ "None" }}
                    {% endif %}
                {% else %}
                    {{ "None" }}
                {% endif %}
            </div>
        </div>
        <div class="black-box" id="timesheet-number">
            <div class="yellow-bar">
                Timesheet Number
            </div>
            <div class="box-content-center">{{ frappe.utils.get_link_to_form(doc.doctype, doc.name) }}</div>
        </div>
    </div>
</div>
<div class="middle-section">
    <div class="middle-black-box">
        <table id="log" class="layout-table">
            <tbody>
                <tr class="layout-row">
                    <td class="layout-cell" id="log-activity-type">
                        <div class="yellow-head-left" style="border-top-left-radius: 2px; border-bottom-left-radius: 2px;">
                            Activity Type
                        </div>
                    </td>
                    <td class="layout-cell" id="log-time-spent">
                        <div class="yellow-head-right">
                            Time Spent
                        </div>
                    </td>
                    <td class="layout-cell" id="log-project">
                        <div class="yellow-head-left">
                            Project
                        </div>
                    </td>
                    <td class="layout-cell" id="log-task">
                        <div class="yellow-head-left">
                            Task
                        </div>
                    </td>
                    <td class="layout-cell" id="log-billing">
                        <div class="yellow-head-right">
                            Billing
                        </div>
                    </td>
                    <td class="layout-cell" id="log-costing">
                        <div class="yellow-head-right" style="border-top-right-radius: 2px; border-bottom-right-radius: 2px;">
                            Costing
                        </div>
                    </td>
                </tr>

{% endmacro %}

{% macro pagefooter() %}
            </tbody>
        </table>
    </div>
</div>
<div id="note-and-space">
    {% if doc.note %}<div class="showmore" style="width: 33px !important;"><img src="/files/note-icon-30.jpg"><span class="showmoretext">{{ doc.note | replace('<br>',' ') | truncate(350,true) }}</span></div>{% endif %}
</div>
<div>
    <div class="section">
        <p class="summary">Timesheet Summary</p>
        <table class="layout-table">
            <tbody>
                <tr class="layout-row">
                    <td class="layout-cell" id="summary1">
                        Billable Hours:
                    </td>
                    <td class="layout-cell" id="summary2">
                        {% if doc.total_billable_hours is divisibleby(1) %}{{ doc.total_billable_hours | int }}{% else %}{{ doc.total_billable_hours | round(2, 'common') }}{% endif %}
                    </td>
                    <td class="layout-cell" id="summaryS">

                    </td>
                    <td class="layout-cell" id="summary1">
                        Billed Hours:
                    </td>
                    <td class="layout-cell" id="summary2">
                        {% if doc.total_billed_hours is divisibleby(1) %}{{ doc.total_billed_hours | int }}{% else %}{{ doc.total_billed_hours | round(2, 'common') }}{% endif %}
                    </td>
                    <td class="layout-cell" id="summaryS">

                    </td>
                </tr>
                <tr class="layout-row">
                    <td class="layout-cell" id="summary1">
                        Billable Amount:
                    </td>
                    <td class="layout-cell" id="summary2">
                        {{ "{:,.2f}".format(doc.total_billable_amount) }}
                    </td>
                    <td class="layout-cell" id="summaryS">

                    </td>
                    <td class="layout-cell" id="summary1">
                        Billed Amount:
                    </td>
                    <td class="layout-cell" id="summary2">
                        {{ "{:,.2f}".format(doc.total_billed_amount) }}
                    </td>
                    <td class="layout-cell" id="summaryS">

                    </td>
                </tr>
                <tr class="layout-row">
                    <td class="layout-cell" id="summary1">
                        Percent Billed:
                    </td>
                    <td class="layout-cell" id="summary2">
                        {% if doc.per_billed is divisibleby(1) %}{{ doc.per_billed | int }}{% else %}{{ doc.per_billed | round(2, 'common') }}{% endif %}%
                    </td>
                    <td class="layout-cell" id="summaryS">

                    </td>
                    <td class="layout-cell" id="summaryT1">
                        Total Time:
                    </td>
                    <td class="layout-cell" id="summaryT2">

                        {% set summary_seconds = doc.total_hours * 3600 %}
                        {% set summary_hours = doc.total_hours | round(0, 'floor') %}
                        {% set rounded_summary_hours = summary_hours * 3600 %}
                        {% set rem_summary_seconds = summary_seconds - rounded_summary_hours %}
                        {% set float_summary_seconds = rem_summary_seconds / 60 %}
                        {% set summary_minutes = float_summary_seconds | round(0, 'common') %}

                        {% if summary_hours > 0 %}{{ summary_hours | int }}h {% endif %}{% if summary_minutes > 0 %}{{ summary_minutes | int }}m{% endif %}
                    </td>
                    <td class="layout-cell" id="summaryS">

                    </td>
                </tr>
            </tbody>
        </table>
    </div>
</div>
{% endmacro %}

{{ pageheader() }}

{% for log in doc.time_logs %}

    {% set total_seconds = log.hours * 3600 %}
    {% set allhours = log.hours | round(0, 'floor') %}
    {% set rounded_hours = allhours * 3600 %}
    {% set rem_seconds = total_seconds - rounded_hours %}
    {% set float_seconds = rem_seconds / 60 %}
    {% set allminutes = float_seconds | round(0, 'common') %}

    {% if log.from_time %}
        {% set time_stamp = frappe.utils.get_datetime(log.from_time) %}
        {% if time_stamp.weekday() == 5 or time_stamp.weekday() == 6 %}
            {% set row_style = 'style=\"background-color: #bed6ff; border-bottom: 1px solid #959595;\"' %}
        {% else %}
            {% set row_style = 'style=\"border-bottom: 1px solid #959595;\"' %}
        {% endif %}
    {% else %}
        {% set row_style = 'style=\"border-bottom: 1px solid #959595;\"' %}
    {% endif %}

    <tr class="layout-row" {{ row_style }}>
        <td class="layout-cell">
            {% if log.description %}
                <div class="cell-content-left">
                    <div class="showmore">
                        {{ log.activity_type }}
                        <span class="showmoretext" style="color: #c8c8c8 !important;">{{ log.description | replace('<br>',' ') | truncate(350,true) }}</span>
                    </div>
                </div>
            {% else %}
                <div class="cell-content-left">{{ log.activity_type }}</div>
            {% endif %}
        </td>
        <td class="layout-cell">
            <div class="cell-content-right">
                <div class="showmore">
                    {% if allhours > 0 %}{{ allhours | int }}h {% endif %}{% if allminutes > 0 %}{{ allminutes | int }}m{% endif %}
                    {% if log.from_time %}
                        {% set todo_from = log.from_time | string %}
                        {% set split_date_time = todo_from.split(' ') %}
                        {% set split_start_date = split_date_time[0] %} 
                        {% set split_start_time = split_date_time[1] %}
                    {% endif %}
                    {% if log.to_time %}
                        {% set todo_to = log.to_time | string %}
                        {% set split_date_time = todo_to.split(' ') %}
                        {% set split_end_date = split_date_time[0] %} 
                        {% set split_end_time = split_date_time[1] %}
                    {% endif %}
                    <span class="showmoretext">
                        {% if split_start_date and split_end_date and split_start_time and split_end_time %}
                            {% if split_start_date == split_end_date %}
                                {{ frappe.utils.formatdate(split_start_date, 'dd-MM-yyyy') }}<hr>
                                {{ split_start_time }} &#8594; {{ split_end_time }}
                            {% else %}
                                {{ frappe.utils.formatdate(split_start_date, 'dd-MM-yyyy') }} {{ split_start_time }}
                                <br>&#8595;<br>
                                {{ frappe.utils.formatdate(split_end_date, 'dd-MM-yyyy') }} {{ split_end_time }}
                            {% endif %}
                        {% endif %}
                    </span>
                </div>
            </div>
        </td>
        <td class="layout-cell">
            <div class="cell-content-left">{% if log.project %}{{ frappe.utils.get_link_to_form('Project', log.project) }}{% endif %}</div>
        </td>
        <td class="layout-cell">
            <div class="cell-content-left">
                {% if log.task %}
                    {% set thistask = frappe.get_doc("Task", log.task) %}
                    {% if thistask.subject %}
                        <div class="showmore">
                            {{ frappe.utils.get_link_to_form('Task', log.task) }}
                            <span class="showmoretext">{{ thistask.subject }}</span>
                        </div>
                    {% else %}
                        {{ log.task }}
                    {% endif %}
                {% endif %}
            </div>
        </td>
        <td class="layout-cell">
            <div class="cell-content-right">{% if log.billing_amount %}{{ "{:,.2f}".format(log.billing_amount) }}{% endif %}</div>
        </td>
        <td class="layout-cell">
            <div class="cell-content-right">{% if log.costing_amount %}{{ "{:,.2f}".format(log.costing_amount) }}{% endif %}</div>
        </td>
    </tr>
{% endfor %}

{{ pagefooter() }}
